import React from 'react';
import {useEffect} from 'react';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Alert from 'react-bootstrap/Alert';
import {useDispatch, useSelector} from 'react-redux';
import {add} from '../store/cartSlice';
import { getProducts } from '../store/productSlice';



export const Product = () => {
    const dispatch = useDispatch();
    // const [products,setProducts] = useState([]);
    const {data : products,status} = useSelector(state =>state.products);

   useEffect(()=>{
    //dispatch a action for fetchproducts
    dispatch(getProducts());

   },[]);
    if(status ==='loading'){
        return <Alert>Loading....</Alert>
    }
    if(status ==='error'){
         return <Alert key={danger}>Something went wrong ! try again Later!!</Alert>
    }
   const addToCart =(product)=>{
      //dispatch an add action
      dispatch(add(product));
   }
    const cards = products.map(product =>(
        <div key={product.id} className='col-md-3' style={{marginBottom:"10px"}}>
                <Card   style={{ width: '28rem',margin:'2px',heigth:"h-100" }}>
                <div className='text-center'>
                    <Card.Img variant="top" src={product.image} style={{width:'100px', height:"130px"}}/>
                </div>
                    <Card.Body>
                        <Card.Title>{product.title}</Card.Title>
                        <Card.Text>
                            USD: {product.price}
                        </Card.Text>
                    </Card.Body>
                    <Card.Footer className='text-center' style={{background:"white"}}>
                        <Button variant="primary" onClick={()=>addToCart(product)} >Add to Cart</Button>
                    </Card.Footer>
                </Card>
        </div>
    ))
  return (
    <div className='row'>
        <div className='text-center'><h1>Product DashBoard</h1></div>  
    {cards}
    </div>
  )
}
